    "topic": "INSERT INTO HYDROPONICS VALUES('"+ msg.epoch_time_now +"'," + msg.value["Water Level"] + "," + msg.value["pH Level"] + "," + msg.value["TDS Level"] + "," + msg.value["Water Temperature"] + ")"
}
