var temp_arr = [];
var column_headers = ["WATER_LEVEL","PH","TDS","WATER_TEMP"]
var combined_arr = [];

for(let j=0;j<column_headers.length;j++){
    for(let i = 0; i < msg.payload.length;i++){
        let temp_msg = msg.payload[i][column_headers[j]];
        let temp_time = Number(msg.payload[i]["TIMESTAMP"]);

        temp_arr.push({"x":temp_time,"y":temp_msg});
        // temp_time +=1;
    }
    combined_arr.push(temp_arr);
    temp_arr = [];
}

msg.payload = [{
    "series":["Water Level","pH","TDS","Water Temperature"],
    "data":combined_arr,
    "labels":[""],
}]
return msg;

